function classes(a,b,c){if(a.classList)switch(b){case"add":a.classList.add(c);break;case"remove":a.classList.remove(c);break;case"toggle":a.classList.toggle(c);break;case"contains":return a.classList.contains(c)?!0:!1;default:console.log("No class actions matched.")}else switch(b){case"add":a.className+=" "+c;break;case"remove":a.className=a.className.replace(new RegExp("(^|\\b)"+c.split(" ").join("|")+"(\\b|$)","gi")," ");break;case"toggle":const d=a.className.split(" "),e=d.indexOf(c);e>=0?d.splice(e,1):d.push(c),a.className=d.join(" ");break;case"contains":return a.className.indexOf(c)>-1?!0:!1;default:console.log("No class actions matched.")}}